<template>
	<view :style="{ paddingTop: statusBarHeight + 'px' }" class="nav-bar" :class="{'show-bg': showBg}">
		<image src="/static/navbar_bg.png" class="navbar-bg" v-if="showBg"></image>
		<view class="nav-bar-inner" @click="onBack">
			<div class="logo" v-if="showLogo">SEEK</div>
			<div class="icon-back" v-if="showBack"></div>
			<text class="title" v-if="title">{{ title }}</text>
			<slot></slot>
		</view>
	</view>
</template>

<script setup>
	import { ref } from 'vue';
	const props = defineProps({
		showLogo: {
			type: Boolean,
			default: () => false
		},
		showBg: {
			type: Boolean,
			default: () => false
		},
		showBack: {
			type: Boolean,
			default: () => false
		},
		title: {
			type: String,
			default: () => ''
		},
		customBack: {
			type: Boolean,
			default: () => false
		}
	});
	const emit = defineEmits(['page-back']);
	const statusBarHeight = uni.getSystemInfoSync().statusBarHeight;
	const onBack = () => {
		if (props.customBack) {
			emit('page-back');
		} else {
			uni.navigateBack();
		}
	};
</script>

